2. Update the UserService to include password recovery functionality:

```csharp
public interface IUserService
{
    // Existing methods...
    Task<bool> RequestPasswordResetAsync(string email);
    Task<bool> ResetPasswordAsync(string email, string token, string newPassword);
}

public class UserService : IUserService
{
    private readonly ApplicationDbContext _context;
    private readonly ILogger<UserService> _logger;
    private readonly IConfiguration _configuration;
    private readonly IEmailService _emailService;

    public UserService(ApplicationDbContext context, ILogger<UserService> logger, IConfiguration configuration, IEmailService emailService)
    {
        _context = context;
        _logger = logger;
        _configuration = configuration;
        _emailService = emailService;
    }

    // Existing methods...

    public async Task<bool> RequestPasswordResetAsync(string email)
    {
        var user = await _context.Users.FirstOrDefaultAsync(u => u.Email == email);
        if (user == null)
        {
            _logger.LogWarning($"Password reset requested for non-existent user: {email}");
            return false;
        }

        var token = GeneratePasswordResetToken();
        user.PasswordResetToken = token;
        user.PasswordResetTokenExpires = DateTime.UtcNow.AddHours(1);

        await _context.SaveChangesAsync();

        await _emailService.SendPasswordResetEmailAsync(email, token);

        _logger.LogInformation($"Password reset token generated for user: {email}");
        return true;
    }

    public async Task<bool> ResetPasswordAsync(string email, string token, string newPassword)
    {
        var user = await _context.Users.FirstOrDefaultAsync(u => u.Email == email && u.PasswordResetToken == token);
        if (user == null || user.PasswordResetTokenExpires < DateTime.UtcNow)
        {
            _logger.LogWarning($"Invalid password reset attempt for user: {email}");
            return false;
        }

        user.PasswordHash = BCrypt.Net.BCrypt.HashPassword(newPassword);
        user.PasswordResetToken = null;
        user.PasswordResetTokenExpires = null;

        await _context.SaveChangesAsync();

        _logger.LogInformation($"Password reset successful for user: {email}");
        return true;
    }

    private string GeneratePasswordResetToken()
    {
        return Guid.NewGuid().ToString("N");
    }
}
```

3. Create an EmailService to handle sending emails:

```csharp
public interface IEmailService
{
    Task SendPasswordResetEmailAsync(string email, string token);
}

public class EmailService : IEmailService
{
    private readonly IConfiguration _configuration;
    private readonly ILogger<EmailService> _logger;

    public EmailService(IConfiguration configuration, ILogger<EmailService> logger)
    {
        _configuration = configuration;
        _logger = logger;
    }

    public async Task SendPasswordResetEmailAsync(string email, string token)
    {
        var message = new MimeMessage();
        message.From.Add(new MailboxAddress("GiveMap Support", _configuration["Email:From"]));
        message.To.Add(new MailboxAddress("", email));
        message.Subject = "Password Reset Request";

        var resetUrl = $"{_configuration["AppUrl"]}/reset-password?token={token}&email={email}";
        message.Body = new TextPart("plain")
        {
            Text = $"To reset your password, please click on this link: {resetUrl}"
        };

        using var client = new SmtpClient();
        await client.ConnectAsync(_configuration["Email:SmtpServer"], 587, false);
        await client.AuthenticateAsync(_configuration["Email:Username"], _configuration["Email:Password"]);
        await client.SendAsync(message);
        await client.DisconnectAsync(true);

        _logger.LogInformation($"Password reset email sent to: {email}");
    }
}
```

4. Update the UserController to include password recovery endpoints:

```csharp
[ApiController]
[Route("api/[controller]")]
public class UserController : ControllerBase
{
    // Existing code...

    [HttpPost("forgot-password")]
    public async Task<IActionResult> ForgotPassword([FromBody] ForgotPasswordModel model)
    {
        if (!ModelState.IsValid)
        {
            return BadRequest(ModelState);
        }

        try
        {
            var result = await _userService.RequestPasswordResetAsync(model.Email);
            if (result)
            {
                return Ok(new { message = "Password reset email sent" });
            }
            return BadRequest(new { message = "Unable to process the request" });
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, $"Error during password reset request: {model.Email}");
            return StatusCode(500, new { message = "An error occurred during the password reset request" });
        }
    }

    [HttpPost("reset-password")]
    public async Task<IActionResult> ResetPassword([FromBody] ResetPasswordModel model)
    {
        if (!ModelState.IsValid)
        {
            return BadRequest(ModelState);
        }

        try
        {
            var result = await _userService.ResetPasswordAsync(model.Email, model.Token, model.NewPassword);
            if (result)
            {
                return Ok(new { message = "Password reset successful" });
            }
            return BadRequest(new { message = "Invalid or expired password reset token" });
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, $"Error during password reset: {model.Email}");
            return StatusCode(500, new { message = "An error occurred during the password reset" });
        }
    }
}

public class ForgotPasswordModel
{
    [Required]
    [EmailAddress]
    public string Email { get; set; }
}

public class ResetPasswordModel
{
    [Required]
    [EmailAddress]
    public string Email { get; set; }

    [Required]
    public string Token { get; set; }

    [Required]
    [MinLength(8)]
    public string NewPassword { get; set; }
}
