3. Update the LocationController:

```csharp
// Controllers/LocationController.cs
[ApiController]
[Route("api/[controller]")]
[Authorize]
public class LocationController : ControllerBase
{
    private readonly ILocationService _locationService;
    private readonly ILogger<LocationController> _logger;
    private readonly IWebHostEnvironment _environment;

    public LocationController(ILocationService locationService, ILogger<LocationController> logger, IWebHostEnvironment environment)
    {
        _locationService = locationService;
        _logger = logger;
        _environment = environment;
    }

    // Existing methods...

    [HttpPut("{id}")]
    public async Task<IActionResult> UpdateLocationDetails(int id, [FromForm] UpdateLocationDetailsModel model)
    {
        if (!ModelState.IsValid)
        {
            return BadRequest(ModelState);
        }

        try
        {
            var imageUrls = new List<string>();
            if (model.Images != null && model.Images.Count > 0)
            {
                foreach (var image in model.Images)
                {
                    var imageUrl = await SaveImageAsync(image);
                    imageUrls.Add(imageUrl);
                }
            }

            var location = await _locationService.UpdateLocationDetailsAsync(id, model.Description, model.Category, imageUrls);
            return Ok(location);
        }
        catch (ArgumentException ex)
        {
            return NotFound(new { message = ex.Message });
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error updating location details");
            return StatusCode(500, new { message = "An error occurred while updating location details" });
        }
    }

    private async Task<string> SaveImageAsync(IFormFile image)
    {
        var uploadPath = Path.Combine(_environment.WebRootPath, "uploads");
        if (!Directory.Exists(uploadPath))
        {
            Directory.CreateDirectory(uploadPath);
        }

        var fileName = Guid.NewGuid().ToString() + Path.GetExtension(image.FileName);
        var filePath = Path.Combine(uploadPath, fileName);

        using (var fileStream = new FileStream(filePath, FileMode.Create))
        {
            await image.CopyToAsync(fileStream);
        }

        return $"/uploads/{fileName}";
    }
}

public class UpdateLocationDetailsModel
{
    [Required]
    [StringLength(1000)]
    public string Description { get; set; }

    [Required]
    [StringLength(50)]
    public string Category { get; set; }

    public List<IFormFile> Images { get; set; }
}
