            .Include(l => l.User)
            .FirstOrDefaultAsync(l => l.Id == locationId);

        if (location == null)
        {
            _logger.LogWarning($"Location not found for ID: {locationId}");
            return null;
        }

        _logger.LogInformation($"Retrieved details for location ID: {locationId}");
        return location;
    }
}
