```csharp
// Services/LocationService.cs
public interface ILocationService
{
    // Existing methods...
    Task<IEnumerable<Location>> FilterLocationsAsync(string category, string urgency, DateTime? fromDate);
}

public class LocationService : ILocationService
{
    private readonly ApplicationDbContext _context;
    private readonly ILogger<LocationService> _logger;

    public LocationService(ApplicationDbContext context, ILogger<LocationService> logger)
    {
        _context = context;
        _logger = logger;
    }

    // Existing methods...

    public async Task<IEnumerable<Location>> FilterLocationsAsync(string category, string urgency, DateTime? fromDate)
    {
        var query = _context.Locations.AsQueryable();

        if (!string.IsNullOrEmpty(category))
        {
            query = query.Where(l => l.Category == category);
        }

        if (!string.IsNullOrEmpty(urgency))
        {
            query = query.Where(l => l.Urgency == urgency);
        }

        if (fromDate.HasValue)
        {
            query = query.Where(l => l.CreatedAt >= fromDate.Value);
        }

        var locations = await query.ToListAsync();

        _logger.LogInformation($"Filtered locations. Category: {category}, Urgency: {urgency}, FromDate: {fromDate}. Found {locations.Count} results.");
        return locations;
    }
}
```

